import "dart:convert";

TermsAndConditionsResponse termsAndConditionsResponseFromJson(String str) =>
    TermsAndConditionsResponse.fromJson(json: json.decode(str));
String termsAndConditionsResponseToJson(TermsAndConditionsResponse data) =>
    json.encode(data.toJson());

class TermsAndConditionsResponse {
  TermsAndConditionsResponse({
    List<Items>? items,
  }) {
    _items = items;
  }

  TermsAndConditionsResponse.fromJson({dynamic json}) {
    if (json["items"] != null) {
      _items = <Items>[];
      json["items"].forEach((dynamic v) {
        _items?.add(Items.fromJson(v));
      });
    }
  }
  List<Items>? _items;
  TermsAndConditionsResponse copyWith({
    List<Items>? items,
  }) =>
      TermsAndConditionsResponse(
        items: items ?? _items,
      );
  List<Items>? get items => _items;

  Map<String, dynamic> toJson() {
    final Map<String, dynamic> map = <String, dynamic>{};
    if (_items != null) {
      map["items"] = _items?.map((Items v) => v.toJson()).toList();
    }
    return map;
  }
}

