Items itemsFromJson(String str) => Items.fromJson(json.decode(str));
String itemsToJson(Items data) => json.encode(data.toJson());

class Items {
  Items({
    int? order,
    List<ContentDetails>? contentDetails,
    String? recordGuid,
  }) {
    _order = order;
    _contentDetails = contentDetails;
    _recordGuid = recordGuid;
  }

  Items.fromJson(dynamic json) {
    _order = json["order"];
    if (json["contentDetails"] != null) {
      _contentDetails = <ContentDetails>[];
      json["contentDetails"].forEach((dynamic v) {
        _contentDetails?.add(ContentDetails.fromJson(v));
      });
    }
    _recordGuid = json["recordGuid"];
  }
  int? _order;
  List<ContentDetails>? _contentDetails;
  String? _recordGuid;
  Items copyWith({
    int? order,
    List<ContentDetails>? contentDetails,
    String? recordGuid,
  }) =>
      Items(
        order: order ?? _order,
        contentDetails: contentDetails ?? _contentDetails,
        recordGuid: recordGuid ?? _recordGuid,
      );
  int? get order => _order;
  List<ContentDetails>? get contentDetails => _contentDetails;
  String? get recordGuid => _recordGuid;

  Map<String, dynamic> toJson() {
    final Map<String, dynamic> map = <String, dynamic>{};
    map["order"] = _order;
    if (_contentDetails != null) {
      map["contentDetails"] =
          _contentDetails?.map((ContentDetails v) => v.toJson()).toList();
    }
    map["recordGuid"] = _recordGuid;
    return map;
  }
}

